INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (10, 'lt_LT', '{WEB_TITLE} - Redagavimo pasirinktys skelbimui, kurio id {ITEM_ID}', '<div><p>Sveiki {USER_NAME},</p>\n<p>Jūs nesate registruotas {WEB_TITLE}, tačiau vis tiek, kurį laiką, galite redaguoti arba pašalinti skelbimą {ITEM_TITLE}.</p>\n<p>Galite redaguoti savo skelbimą paspaudę ant sekančios nuorodos: {EDIT_LINK}</p>\n<p>Galite ištrinti skelbimą paspaudę ant sekančios nuorodos: {DELETE_LINK}</p>\n\n<p>Užsiregistravę mūsų sistemoje gausite pilną priėjimą prie redagavimo įrankių.</p>\n<p>Pagarbiai,</p>\n{WEB_TITLE}</div>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (11, 'lt_LT', '{WEB_TITLE} - Patvirtinkite savo skelbimą', '<p>Sveiki {USER_NAME},</p>\n<p>Jūs gavote šį elektroninį laišką, nes {WEB_TITLE} buvo pridėtas naujas skelbimas. Prašome jį patvirtinti nuspausdžiant nuorodą, esančią šio elektroninio laiško pabaigoje. Jei šį skelbimą paskelbėte ne jūs, ignoruokite šį elektroninį laišką.</p>\n<p>Skelbimo detalės:</p>\n<p>Kontaktinis vardas: {USER_NAME}<br>Kontaktinis el. paštas: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Kaina: {ITEM_PRICE}<br>Šalis: {ITEM_COUNTRY}<br>Regionas: {ITEM_REGION}<br>Miestas: {ITEM_CITY}<br>Url: {ITEM_URL}<br><br>Patvirtinkite savo skelbimą: {VALIDATION_LINK}</p>\n<p>Pagarbiai,</p>\n<p>{WEB_TITLE}</p>'); 
