INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (1, 'lv_LV', '{WEB_TITLE} - Kāds ir uzdevis jautājumu par Jūsu sludinājumu', '<p>Sveiks {CONTACT_NAME}!</p>\n<p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) atsūtīja Jums ziņojumu par Jūsu sludinājumu :  {ITEM_TITLE}:</p>\n<p>{COMMENT}</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (2, 'lv_LV', 'Lūdzu apstipriniet savu epastu priekš {WEB_TITLE}', '<p>Sveiks {USER_NAME},</p>\n<p>Lūdzu apstipriniet savu epastu: {VALIDATION_LINK}</p>\n<p>Paldies!</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (3, 'lv_LV', '{WEB_TITLE} - Veiksmīga reģistrācija!', '<p>Sveiki {USER_NAME},</p>\n<p>Jūs veiksmīgi reģistrējāties {WEB_TITLE}.</p>\n<p>Paldies!</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (4, 'lv_LV', 'Skaties, ko es atradu {WEB_TITLE}', '<p>Sveiks {FRIEND_NAME},</p>\n<p>Jūsu draugs {USER_NAME} vēlas padalīties ar Jums:</p>\n<p>{ITEM_URL}</p>\n<p>Ziņojums:</p>\n<p>{COMMENT}</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (5, 'lv_LV', '{WEB_TITLE} - Jauni sludinājumi pēdējās stundas laikā', '<p>Sveicināti {USER_NAME},</p>\n<p>Jauni sludinājumi tika izvietoti pēdējās stundas laikā. Apskatieties:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Lai atteiktos no paziņojumiem: {UNSUB_LINK}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (6, 'lv_LV', '{WEB_TITLE} - Šodienas sludinājumi', '<p>Sveiki {USER_NAME},</p>\n<p>Jauni sludinājumi tika ievietoti pēdējās dienas laikā:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Lai atteiktos no paziņojumiem , sekojiet saitei: {UNSUB_LINK}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (7, 'lv_LV', '{WEB_TITLE} - Jauni sludinājumi nedēļas laikā', '<p>Sveiki {USER_NAME},</p>\n<p>Jauni sludinājumi tika publicēti nedēļas laikā:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Lai atteiktos no paziņojumiem , sekojiet saitei: {UNSUB_LINK}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (8, 'lv_LV', '{WEB_TITLE} - Jauns sludinājums', '<p>Sveiki {USER_NAME},</p>\n<p>Tika publicēts jauns sludinājums:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Lai atteiktos no paziņojumiem , sekojiet saitei: {UNSUB_LINK}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (9, 'lv_LV', '{WEB_TITLE} - Jauns komentārs Jūsu sludinājumam', '<p>Ir jauns komentārs Jūsu sludinājumam <br>URL: {ITEM_URL}</p>\n<p>Title: {COMMENT_TITLE}<br>Comment: {COMMENT_TEXT}<br>Autors: {COMMENT_AUTHOR}<br>Autora epasts: {COMMENT_EMAIL}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (10, 'lv_LV', '{WEB_TITLE} - Labot savu sludinājumu', '<div><p>Sveiki {USER_NAME},</p>\n<p>Jūs neesat reģistrēts {WEB_TITLE}, bet Jūs varat izmainīt savu sludinājumu {ITEM_TITLE} .</p>\n<p>Lai labotu Jūsu sludinājumu, sekojiet šai saitei: {EDIT_LINK}</p>\n<p>Jūs varat izdzēst savu sludinājumu: {DELETE_LINK}</p>\n\n<p>Ja Jūs reģistrēsieties, jums būs pilna pieeja visiem saviem sludinājumiem.</p>\n<p>Ar cieņu,</p>\n{WEB_TITLE}</div>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (11, 'lv_LV', '{WEB_TITLE} - Apstipriniet savu sludinājumu', '<p>Sveiki {USER_NAME},</p>\n<p>Jūs esat publicējis sludinājumu {WEB_TITLE}. Lūdzu apstipriniet, lai to varētu redzēt citi lietotāji.</p>\n<p>Sludinājums:</p>\n<p>Vārds: {USER_NAME}<br>Epasts: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Cena: {ITEM_PRICE}<br>Valsts: {ITEM_COUNTRY}<br>Reģions: {ITEM_REGION}<br>Pilsēta: {ITEM_CITY}<br>Saite: {ITEM_URL}<br><br>Apstipriniet savu sludinājumu: {VALIDATION_LINK}</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (12, 'lv_LV', '{WEB_TITLE} - Jauns sludinājums', '<div><p>Dārgais {WEB_TITLE} admins,</p>\n<p>Jūs saņemat šo epastu, jo jauns sludinājums tika publicēts Jūsu {WEB_TITLE}.</p>\n<p>Sludinājums:</p>\n<p>Vārds: {USER_NAME}<br>Epasts: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Cena: {ITEM_PRICE}<br>Valsts: {ITEM_COUNTRY}<br>Reģions: {ITEM_REGION}<br>Pilsēta: {ITEM_CITY}<br>Saite: {ITEM_URL}<br><br>Lai labotu sludinājumu, sekojiet norādei: {EDIT_LINK}</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p></div>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (13, 'lv_LV', '{WEB_TITLE} - Atjaunot Jūsu paroli', '<p>Sveiki {USER_NAME},</p>\n<p>Jūs pieprasījāt paroles maiņu, lai tuprinātu spiediet šo saiti: {PASSWORD_LINK}</p>\n<p>Links tiks izdzēsts pēc 24 stundām.</p>\n<p>Ja Jūs nepieprasījāt paroles maiņu, tad ignorējiet šo epastu. IP no kura pieprasīja paroles maiņu : {IP_ADDRESS} on {DATE_TIME}</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (14, 'lv_LV', '{WEB_TITLE} - Jūsu pieprasījums epasta maiņai', '<p>Sveiki {USER_NAME}</p>\n<p>Jūs pieprasījāt epasta maiņu, apstipriniet to: {VALIDATION_LINK}</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (15, 'lv_LV', 'Lūdzu apstipriniet savu pieteikumu', '<p>Sveiki {USER_NAME},</p>\n<p>Jūsu paziņojums ir reģistrēts, apstipriniet to: {VALIDATION_LINK}</p>\n<p>Paldies!</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (16, 'lv_LV', '{WEB_TITLE} - Jūsu komentārs ir apstiprināts', '<p>Sveiki {COMMENT_AUTHOR},</p>\n<p>Jūsu komentārs ir publicēts: {ITEM_URL}</p>\n<p>Ar cieņu,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (17, 'lv_LV', '{WEB_TITLE} - Sludinājums apstiprināts', '<p>Labdien {USER_NAME},</p>\n<p>Jūs saņemt šo vēstuli jo sludinājums ir publicēts {WEB_TITLE}. Lūdzu apstipriniet šo sludinājumu, klikšķinot uz saites nedaudz zemāk. Ja sludinājums nav jūsu lūdzu ignorējiet šo vēstuli.</p>\n<p>Sludinājuma detaļas:</p>\n<p>Kontakta vārds: {USER_NAME}<br />Kontakta epasts: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Cena: {ITEM_PRICE}<br />Valsts: {ITEM_COUNTRY}<br />Reģions: {ITEM_REGION}<br />Pilsēta: {ITEM_CITY}<br />Url: {ITEM_URL}<br /><br />Apstipriniet savu sludinājumu: {VALIDATION_LINK}</p>\n\n<p>Jūs neesat reģistrējies {WEB_TITLE}, tomēr neilgu brīdi jūs varēsiet rediģēt vai dzēst savu sludinājumu {ITEM_TITLE}.</p>\n<p>Jūs varat rediģēt sludinājumu klikšķinot šo saiti: {EDIT_LINK}</p>\n<p>Jūs varat dzēs sludinājumu klikšķinot šo saiti: {DELETE_LINK}</p>\n\n<p>ja esat reģistrējies jums ir pilna pieja rediģēšanas opcijām.</p>\n<p>Veiksmes,</p>\n{WEB_TITLE}'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (18, 'lv_LV', '{WEB_TITLE} - Jauns lietotājs', '<div><p>{WEB_TITLE} administratoram,</p>\n<p>Šis epasts ir izsūtīts jo jauns lietotājs ir izveidojis kontu {WEB_TITLE}.</p>\n<p>Lietotāja detaļas:</p>\n<p>Vārds: {USER_NAME}<br />epasts: {USER_EMAIL}</p>\n<p>Veiksmes,</p>\n<p>{WEB_TITLE}</p></div>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (19, 'lv_LV', '{WEB_TITLE} - Kādam ir jums jautājums', '<p>Labdien {CONTACT_NAME}!</p>\n<p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) ir rakstijis jums ziņu:</p>\n<p>{COMMENT}</p>\n<p>Veiksmes,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (20, 'lv_LV', '{WEB_TITLE} - Jauns komentārs sludinājumam: id {ITEM_ID}', '<p>Ir jauns komentārs sludinājumam: id {ITEM_ID} <br />URL: {ITEM_URL}</p>\n<p>Virsraksts: {COMMENT_TITLE}<br />Komentārs: {COMMENT_TEXT}<br />Autors: {COMMENT_AUTHOR}<br />Autora epasts: {COMMENT_EMAIL}</p>'); 
