INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (1, 'pt_PT', '{WEB_TITLE} - Alguém tem uma pergunta sobre o artigo', '<p>Olá {CONTACT_NAME}!</p>\\n<p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE})deixou-lhe uma mensagem sobre o artigo{ITEM_TITLE}:</p>\\n<p>{COMMENT}</p>\\n<p>Atenciosamente,</p>\\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (2, 'pt_PT', 'Valide a sua conta no site {WEB_TITLE}', '<p>Olá {USER_NAME},</p>\\n<p>Por favor, valide o seu registo clicando no link a seguir: {VALIDATION_LINK}</p>\\n<p>Obrigado!</p>\\n<p>Atenciosamente,</p>\\n<p>{WEB_TITLE}</p>'); 
