INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (4, 'pt_PT', 'Olha o que descobri no {WEB_TITLE}', '<p>Olá {FRIEND_NAME},</p>\\n<p>O teu amigo {USER_NAME} quer partilhar este anuncio contigo:</p>\\n<p>{ITEM_URL}</p>\\n<p>Mensagem:</p>\\n<p>{COMMENT}</p>\\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (5, 'pt_PT', '{WEB_TITLE} - Novos anúncios nesta ultima hora', '<p>Olá {USER_NAME},</p>\\n<p>Novos anúncios foram publicados na última hora. Dê uma olhada neles:</p>\\n<p>{ADS}</p>\\n<p>-------------</p>\\n<p>Para deixar de receber estes alertas carregue neste link: {UNSUB_LINK}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (6, 'pt_PT', '{WEB_TITLE} - Novos anúncios nos últimos dias', '<p>Olá {USER_NAME},</p>\\n<p>Novos anúncios foram publicados no último dia. Dê uma olhada neles:</p>\\n<p>{ADS}</p>\\n<p>-------------</p>\\n<p>Para deixar de receber estes alertas siga este link: {UNSUB_LINK}</p>'); 
