INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (1, 'sk_SK', '{WEB_TITLE} - Niekto má otázku ohľadom Vášho inzerátu', '<p>Dobrý deň {CONTACT_NAME}!</p>\n<p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) zanechal správu k Vášmu inzerátu {ITEM_TITLE}:</p>\n<p>{COMMENT}</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (2, 'sk_SK', 'Proím potvrďte Váš účet na {WEB_TITLE}', '<p>Dobrý deň {USER_NAME},</p>\n<p>Prosím potvrďte Vašu registráciu klilknutím na nasledujúci odkaz: {VALIDATION_LINK}</p>\n<p>Ďakujeme!</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (3, 'sk_SK', '{WEB_TITLE} - Registrácia úspešná!', '<p>Dobrý deň {USER_NAME},</p>\n<p>Úspešne ste sa zaregistrovali na {WEB_TITLE}.</p>\n<p>Ďakujeme!</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (4, 'sk_SK', 'Pozri čo som objavil na {WEB_TITLE}', '<p>Dobrý deň {FRIEND_NAME},</p>\n<p>Váš priateľ {USER_NAME} chce s Vami zdieľať nasledujúci inzerát:</p>\n<p>{ITEM_URL}</p>\n<p>Správa:</p>\n<p>{COMMENT}</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (5, 'sk_SK', '{WEB_TITLE} - Nové inzeráty za poslednú hodinu', '<p>Dobrý deň {USER_NAME},</p>\n<p>Pribudli nové inzeráty za poslednú hodinu. Pozrite sa na ne:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Pre odhlásenie z tohto upozornenia, kliknite na: {UNSUB_LINK}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (6, 'sk_SK', '{WEB_TITLE} - Pribudli nové inzeráty za posledný deň', '<p>Dobrý deň {USER_NAME},</p>\n<p>Pribudli nové inzeráty za posledný deň. Pozrite sa na ne:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Pre odhlásenie z tohto upozornenia, kliknite na: {UNSUB_LINK}</p>'); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (7, 'sk_SK', '{WEB_TITLE} - Nové inzeráty za posledný týždeň', '<p>Dobrý deň {USER_NAME},</p>\n<p>Nové inzeráty pribudli za posledný týždeň. Pozrite sa na ne: </p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Pre odhlásenie z tohto upozornenia, kliknite na: {UNSUB_LINK}</p> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (8, 'sk_SK', '{WEB_TITLE} - Nový inzerát', '<p>Dobrý deň {USER_NAME},</p>\n<p>Pribudol nový inzerát, pozrite sa naňho: </p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>Pre odhlásenie z tohto upozornenia, kliknite na: {UNSUB_LINK}</p> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (9, 'sk_SK', '{WEB_TITLE} - Nový komentár k inzerátu s ID {ITEM_ID}', '<p>Pribudol nový komentár k inzerátu s ID {ITEM_ID} <br>URL: {ITEM_URL}</p>\n<p>Názov: {COMMENT_TITLE}<br>Komentár: {COMMENT_TEXT}<br>Autor: {COMMENT_AUTHOR}<br>Autorov e-mail: {COMMENT_EMAIL}</p> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (10, 'sk_SK', '{WEB_TITLE} - Upravte nastavenia pre inzerát s ID {ITEM_ID}', '<div><p>Dobrý deň {USER_NAME},</p>\n<p>Nie ste registrovaní na {WEB_TITLE}, ale stále môžete upravovať alebo mazať inzerát {ITEM_TITLE} na krátky čas.</p>\n<p>Môžete upraviť inzerát kliknutím na nasledujúci odkaz: {EDIT_LINK}</p>\n<p>Môžete zmazať inzerát kliknutím na nasledujúci odkaz: {DELETE_LINK}</p>\n\n<p>Ak sa zaregistrujete, budete mať úplný prístup k možnostiam úprav inzerátov.</p>\n<p>S pozdravom,</p>\n{WEB_TITLE}</div> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (11, 'sk_SK', '{WEB_TITLE} - Potvrďte váš inzerát', '<p>Dobrý deň {USER_NAME},</p>\n<p>Dostali ste tento e-mail, pretože ste uverejnili inzerát na {WEB_TITLE}. Prosím potvrďte ho kliknutím na odkaz na konci tohto e-mailu. Ak ste tento inzerát neuverejnili Vy, prosím ignorujte tento e-mail.</p>\n<p>Detaily inzerátu:</p>\n<p>Meno: {USER_NAME}<br>E-mail: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Cena: {ITEM_PRICE}<br>Krajina: {ITEM_COUNTRY}<br>Región: {ITEM_REGION}<br>Mesto: {ITEM_CITY}<br>Url: {ITEM_URL}<br><br>Potvrďte Váš inzerát: {VALIDATION_LINK}</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (12, 'sk_SK', '{WEB_TITLE} - Nový inzerát', '<div><p>Drahý {WEB_TITLE} admin,</p>\n<p>Dostali ste tento e-mail, pretože bol zverejnený nový inzerát na {WEB_TITLE}.</p>\n<p>Detaily inzerátu:</p>\n<p>Meno: {USER_NAME}<br>E-mail: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Cena: {ITEM_PRICE}<br>Krajina: {ITEM_COUNTRY}<br>Región: {ITEM_REGION}<br>Mesto: {ITEM_CITY}<br>Url: {ITEM_URL}<br><br>Môžete upraviť tento inzerát kliknutím na nasledujúci odkaz: {EDIT_LINK}</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p></div> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (13, 'sk_SK', '{WEB_TITLE} - Obnovte svoje heslo', '<p>Dobrý deň {USER_NAME},</p>\n<p>Poslali sme Vám tento e-mail, pretože ste požiadali o obnovenie hesla. Pre obnovenie hesla kliknite na nasledujúci odkaz: {PASSWORD_LINK}</p>\n<p>Odkaz bude deaktivovaný po 24 hodinách.</p>\n<p>Ak ste nepožiadali o obnovenie hesla, ignorujte túto správu. Požiadavka na obnovenie hesla bola odoslaná z IP adresy {IP_ADDRESS} dňa {DATE_TIME}</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (14, 'sk_SK', '{WEB_TITLE} - Vyžiadali ste si zmenu hesla', '<p>Dobrý deň {USER_NAME}</p>\n<p>Obdržali ste tento e-mail, pretože ste požiadali o zmenu e-mailovej adresy. Prosím potvrďte túto novú e-mailovú adresu kliknutím na nasledujúci odkaz: {VALIDATION_LINK}</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (15, 'sk_SK', 'Prosím potvrďte Vaše upozornenie', '<p>Dobrý deň {USER_NAME},</p>\n<p>Prosím potvrďte Vaše upozornenie kliknutím na nasledujúci odkaz: {VALIDATION_LINK}</p>\n<p>Ďakujeme!</p>\n<p>S pozdravom,</p>\n<p>{WEB_TITLE}</p> '); 
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text)  VALUES (16, 'sk_SK', '{WEB_TITLE} - Váš komentár bol schválený', '<p>Dobrý deň {COMMENT_AUTHOR},</p>\n<p>Váš komentár k inzerátu bol schválený: {ITEM_URL}</p>\n<p>S pozdravom,</p>\n&lt;{WEB_TITLE}<p></p>'); 
